/**
 * {{Template}} - Restituisce il template elaborato
 * @param {string} string Testo da processare
 * @param {string[]} flags Delimitatori del valore, default ['{{', '}}'] 
 */
function Template(string, flags = ['{{', '}}']) {
    if (string === null || string === undefined || typeof string !== 'string') return string
    if (string.trim() == '') return string
    let re = new RegExp(flags[0] + '(.*?)' + flags[1], 'gmi')
    let match = string.match(re)
    if (!match) return string
    if (match && match.length > 0) {
        for (let index = 0; index < match.length; index++) {
            const element = match[index]
            let value = JSGet(element.substr(flags[0].length, element.length - (flags[0].length + flags[1].length)))
            string = String.raw`${string}`.replace(element, value)
        }

    }

    return string
}

/**
 * Ricerca le chievi in un oggetto JSON e restituisce il valore o le proprietÃ 
 * @param {string | string []} args args = 'key' || 'key.subkey.{what}' || 'key.*.{what}' || ['key', 'subkey', '{what}'] || ['key', '*', '{what}']
 * @param {string | string []} args {what} = $type || $keys || $first_key || $last_key || $root || $boolean || $object || $...> (ultimo risultato)
 * @param {NodeMessage | {} | []} object Oggetto sul quale effettuare la ricerca
 */
function JSGet(args, object = msg) {
    //creo una copia di object
    object = RED.util.cloneMessage(object)

    //definisci le chiavi
    if (!args || (typeof args === 'string' && !args.trim())) {
        return null
    } else if (typeof args === 'string') {
        //crea una array di chiavi
        args = args.split('.')
    } else if (!Array.isArray(args)) {
        return null
    }

    //chiave primaria
    var key = args[0]
    if (!key) return null
    if (typeof key !== 'string') return null
    if (key == '') return null

    //chiave secondaria
    var subKey = (args.length == 2) ? args[1] : null
    if (subKey && (subKey == '*' || subKey == '')) subKey = null

    //risultato richiesto
    var what = (args.length == 3) ? args[2] : ''

    //definisci l'oggetto, deve essere un JSON
    if (object && typeof object === 'object' && Array.isArray(object) && object.length > 0) {
        //se Ã¨ una array lo converto
        Object.assign({}, object)
    } else if (typeof object === 'string') {
        //se Ã¨ un JSON string lo converto
        try {
            object = JSON.parse(object)
        } catch (error) {
            return null
        }
    }

    //se non Ã¨ un JSON restituisco null
    if (!object || (Object.keys(object).length == 0 && object.constructor === Object)) {
        return null
    }

    //risultati della ricerca
    var result = null

    //ricerca in profonditÃ  la chiave nell'oggetto
    var searchDeep = function SearchDeep(/** @type {{ [x: string]: any; hasOwnProperty: (arg0: string) => any; }} */ object, /** @type {string} */ rootKeys) {
        //percorso della chiave
        var root = function GetRootKey(/** @type {string} */ addKey) {
            return rootKeys ? `${rootKeys}.${addKey}` : addKey
        }

        //chiave trovata
        if (object && object.hasOwnProperty(key)) {
            //se vuota assegna key
            if (!rootKeys) rootKeys = key

            if (subKey && object[key].hasOwnProperty(subKey)) {
                result = {
                    value: object[key][subKey],
                    type: typeof object[key][subKey],
                    keys: rootKeys.split('.').length,
                    first_key: rootKeys.split('.')[0],
                    last_key: (rootKeys.split('.').length > 0) ? rootKeys.split('.')[rootKeys.split('.').length - 1] : rootKeys.split('.')[0],
                    root: rootKeys
                }
            } else if (!subKey) {
                result = {
                    value: object[key],
                    type: typeof object[key],
                    keys: rootKeys.split('.').length,
                    first_key: rootKeys.split('.')[0],
                    last_key: (rootKeys.split('.').length > 0) ? rootKeys.split('.')[rootKeys.split('.').length - 1] : rootKeys.split('.')[0],
                    root: rootKeys
                }
            }
        }

        //ricerca della chiave
        if (!result || what.endsWith('>')) {
            for (let k in object) {
                if (object.hasOwnProperty(k) && typeof object[k] === 'object') {
                    searchDeep(object[k], root(k))
                }
            }
        }
    }

    //inizia la ricerca
    searchDeep(object)

    //restituisci il risultato
    switch (what.replace('>', '')) {
        case "$type":
            return (!result ? null : result.type)
        case "$keys":
            return (!result ? null : result.keys)
        case "$first_key":
            return (!result ? null : result.first_key)
        case "$last_key":
            return (!result ? null : result.last_key)
        case "$root":
            return (!result ? null : result.root)
        case "$boolean":
            return !!result
        case "$object":
            return (!result ? null : result)
        default:
            return (!result ? null : result.value)
    }
}

/**
 * Verifica se un oggetto Ã¨ di tipo numerico
 * @param {object} object Oggetto da verificare
 */
function IsNumber(object) {
    if (object === null || object === undefined) return false
    if ((typeof object === 'number' && isFinite(object)) || Object.prototype.toString.apply(object) === '[object Number]') return true
    return (typeof object === "string" && !isNaN(object) && !isNaN(parseFloat(object)))
}

