if (latitude !== null && longitude !== null) {
    latitude = Template(`{{${latitude}}}`)
    longitude = Template(`{{${longitude}}}`)

    if (IsNumber(latitude) && IsNumber(longitude)) {
        latitude = parseFloat(latitude)
        longitude = parseFloat(longitude)
        node.status({ fill: "blue", shape: "ring", text: "Request" })
        msg.original_message = RED.util.cloneMessage(msg)
        msg.method = 'GET'
        //https://open-meteo.com/en/docs#api-documentation
        msg.url = `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&models=best_match&current_weather=true&timezone=auto`
        return [msg, null]
    }
}

node.status({ fill: "blue", shape: "dot", text: "Ready" })
return [null, msg]

